<script>
function toggleClass(e, className, activeClass, title_1, title_2) {
    let element = document.querySelector(className);
    if (element) {
        if (element.classList.contains(activeClass)) {
            e.querySelector('span').innerText = title_2;
        } else {
            e.querySelector('span').innerText = title_1;
        }
        element.classList.toggle(activeClass);
    }
}
</script>

