    $('.body_vitri_all_diadiem_1').removeClass('hello_butt');
    $('.body_vitri_all_diadiem_1').removeClass('icon_fill');
    $(a).addClass('hello_butt');
    $(a).addClass('icon_fill');
}


function click_hello(duy) {
    $('.body_vitri_all2_content_school_top1').removeClass('hello_kitty');
    $(duy).addClass('hello_kitty');
}

function click_matbang(matbang) {
    $('.body_matbang_khoi1_option').removeClass('main_background');
    $(matbang).addClass('main_background');
}
</script>


